require "pry"
require_relative "../lib/day_two"

RSpec.describe DayTwo do
  INPUT = [
    "",
    "",
    "",
    "",
    "",
  ]

  describe "solve" do
    describe "solve_part_one" do
      it "works for the example input" do
        expect(DayTwo.new(["ULL", "RRDDD", "LURDL", "UUUUD"]).solve_part_one).to eq("1985")
      end

      it "returns the correct solution" do
        expect(DayTwo.new(INPUT).solve_part_one).to eq("18843")
      end
    end

    describe "solve_part_two" do
      it "works for the example input" do
        expect(DayTwo.new(["ULL", "RRDDD", "LURDL", "UUUUD"]).solve_part_two).to eq("5DB3")
      end

      it "returns the correct solution" do
        expect(DayTwo.new(INPUT).solve_part_two).to eq("67BB9")
      end
    end
  end
end
